const apiResult = $input.first().json;
const webhookData = $('Webhook触发').first().json.body || {};

// 检查 API 错误
if (apiResult.error) {
  return [{ json: { 
    success: false, 
    error: apiResult.error.message || 'OpenAI API 调用失败',
    code: apiResult.error.code
  } }];
}

// 处理返回的 Keys 列表
const keys = apiResult.data || [];

// 转换为我们的格式
const formattedKeys = keys.map(key => ({
  id: key.id,
  name: key.name,
  redacted_value: key.redacted_value,
  created_at: key.created_at ? new Date(key.created_at * 1000).toISOString() : null,
  owner: key.owner,
  platform: 'openai',
  project_id: webhookData.project_id
}));

return [{ json: { 
  success: true,
  keys: formattedKeys,
  total: formattedKeys.length,
  has_more: apiResult.has_more || false
} }];
